function forumPosterRanks(t) {
	(posts = []),
		(usernames = []),
		(counts = []),
		(reps = [0, 0, 0, 0]),
		(i = [1, 1, 1, 1]),
		(j = 0),
		(final = []),
		(returnVal = {}),
		t.forEach((t, s) => {
			fetch("https://scratchdb.lefty.one/v2/forum/search/?q=%2Btopic:" + t + "&o=oldest")
				.then((t) => t.text())
				.then((n) => {
					for (reps[s] = Math.floor(JSON.parse(n).hits / 50) + 1,
							posts = posts.concat(JSON.parse(n).posts),
							j++,
							i[s] = 1; i[s] < reps[s]; i[s]++) {
						fetch("https://scratchdb.lefty.one/v2/forum/search/?q=%2Btopic:" + t + "&o=oldest&page=" + i[s])
							.then((t) => t.text())
							.then((t) => {
								if (((posts = posts.concat(JSON.parse(t).posts)),            j++,            reps.reduce((t, e) => t + e) == j)
								) {
									for (var s in (posts.forEach((t, e) => {
										usernames.push(posts[e].username);
									}),
									usernames.forEach((t) => {
										NULL == counts[t] ? (counts[t] = 1) : ++counts[t];
									}),
									counts)) {
										final.push([s, counts[s]]);
									}

									final.sort(function (t, e) {
										return (t = t[1]), (e = e[1]), t < e ? -1 : t > e ? 1 : 0;
									}),
										final.reverse(),
										(document.getElementById("load").outerHTML = "");
									for (var n = 0; n < final.length; n++) {
										(e = document.createElement("tr")),
											(e.innerHTML =
												"<td>" +
												(n + 1) +
												"</td><td>" +
												final[n][0] +
												"</td><td>" +
												final[n][1] +
												"</td>"),
											document.getElementById("table").appendChild(e);
									}
								}

							});
					}

				});
		});
}
function addInput() {
	(e = document.createElement("input")),
		e.setAttribute("type", "text"),
		e.setAttribute("name", "thread"),
		document.getElementById("threads").insertBefore(e, document.getElementById("brAdd"));
}
